CREATE DATABASE UNIVERSITY;						/*СТВОРЮЄМО БАЗУ ДАНИХ UNIVERSITY*/  USE UNIVERSITY									/* ОБИРАЄМО БАЗУ UNIVERSITY ДЛЯ РОБОТИ*/  CREATE TABLE Students (							/*СТВОРЮЄМО ТАБЛИЦЮ Students*/ 	StudentID INT PRIMARY KEY,					/*ЗАДАЄМ ПЕРВІСНИЙ КЛЮЧ В КОЛОНЦІ STUDENTID ТА АТРИБУТИ (ЦЕЙ ТА 3 РЯДКИ НИЖЧЕ)*/ 	FirstName NVARCHAR(50),			 	LastName NVARCHAR(50), 	EnrollmentDate DATE 	);  	CREATE TABLE Courses (						/*СТВОРЮЄМО ТАБЛИЦЮ Courses*/ 	CourseID INT PRIMARY KEY,					/*ЗАДАЄМ ПЕРВІСНИЙ КЛЮЧ В КОЛОНЦІ CourseID ТА АТРИБУТИ (ЦЕЙ ТА 3 РЯДКИ НИЖЧЕ)*/ 	CourseName NVARCHAR(50),			 	CourseDescription NVARCHAR(200), 	Credits INT 	);  	INSERT INTO Students(										/*ВВОДИМО 3 РЯДКИ ДАНИХ В БАЗУ ТАБЛИЦЮ Students*/ 	StudentID, FirstName, LastName, EnrollmentDate) 	VALUES(1, 'Олександр', 'Мірошніченко', '2024-09-10'); INSERT INTO Students  	VALUES(2, 'Марія', 'Іваненко', '2024-09-10'); INSERT INTO Students  	VALUES(3, 'Василь', 'Коваленко', '2024-09-10');  	INSERT INTO Courses(										/*ВВОДИМО 3 РЯДКИ ДАНИХ В БАЗУ ТАБЛИЦЮ Courses*/ 	CourseID, CourseName, CourseDescription, Credits) 	VALUES(1, 'Маркетинг', 'Маркетологічні дослідження', 50); INSERT INTO Courses  	VALUES(2, 'Бізнес-аналітика', 'Аналітика бізнес-процесів на підприємстві', 75); INSERT INTO Courses  	VALUES(3, 'Моніторинг', 'Моніторинг складних економічних систем', 120);   	SELECT* FROM Students;										/*ВИБІРКА ДАНИХ З ТАБЛИЦЬ*/ 	SELECT* FROM Courses;  	ALTER TABLE Students ADD Email NVARCHAR(50);				/*ДОДАЄМО СТОВПЦІ ДО ТАБЛИЦЬ*/ 	ALTER TABLE Courses ADD Department NVARCHAR(50);  	SELECT* FROM Students;										/*ВИБІРКА ДАНИХ З ТАБЛИЦЬ*/ 	SELECT* FROM Courses;  	DROP DATABASE UNIVERSITY;									/*ВИДАЛЯЄМО БАЗУ ДАНИХ*/   	